import React, { useEffect, useState } from "react";
import axios from "axios";
import { useParams } from "react-router-dom";

const TaskComponent = () => {
  const { userId } = useParams();
  const [tasks, setTasks] = useState([]);
  const [userName, setUserName] = useState("");
  const [filter, setFilter] = useState("all");
  const [isLoading, setIsLoading] = useState(false);
  const [message, setMessage] = useState("");

  useEffect(() => {
    fetchTasks(1);
  }, [userId]);

  const fetchTasks = async (userId) => {
    setIsLoading(true);
    try {
      const response = await axios.get(`${process.env.REACT_APP_API_URL}/api/tasks/${userId}`);
      setTasks(response.data.tasks);
      setUserName(`${response.data.user.firstName} ${response.data.user.lastName}`);
      setMessage("");
    } catch (error) {
      setMessage("Error fetching tasks. Please try again later.");
    } finally {
      setIsLoading(false);
    }
  };

  const updateTaskStatus = async (taskId, newStatus) => {
    setIsLoading(true);
    try {
      await axios.put(`${process.env.REACT_APP_API_URL}/api/tasks/${userId}/${taskId}`, { status: newStatus });
      setTasks((prevTasks) =>
        prevTasks.map((task) =>
          task.taskId === taskId ? { ...task, status: newStatus } : task
        )
      );
      setMessage("Task status updated successfully.");
    } catch (error) {
      setMessage("Error updating task status. Please try again later.");
    } finally {
      setIsLoading(false);
    }
  };

  const filteredTasks = tasks.filter((task) => {
    if (filter === "all") return true;
    return filter === "completed" ? task.status : !task.status;
  });

  return (
    <div className="p-4">
      <h1 className="text-xl font-bold mb-4">Welcome, {userName}</h1>
      <label htmlFor="filter" className="block mb-2 font-medium">Filter Tasks:</label>
      <select
        id="filter"
        value={filter}
        onChange={(e) => setFilter(e.target.value)}
        className="border p-2 mb-4"
      >
        <option value="all">All</option>
        <option value="completed">Completed</option>
        <option value="pending">Pending</option>
      </select>

      {isLoading ? (
        <p>Loading...</p>
      ) : (
        <table className="w-full border-collapse border border-gray-300">
          <thead>
            <tr>
              <th className="border p-2">Task Name</th>
              <th className="border p-2">Status</th>
              <th className="border p-2">Action</th>
            </tr>
          </thead>
          <tbody>
            {filteredTasks.map((task) => (
              <tr key={task.taskId}>
                <td className="border p-2">{task.taskName}</td>
                <td className="border p-2">{task.status ? "Completed" : "Pending"}</td>
                <td className="border p-2">
                  <input
                    type="checkbox"
                    checked={task.status}
                    onChange={() => updateTaskStatus(task.taskId, !task.status)}
                    disabled={isLoading}
                  />
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      )}

      {message && <p className="mt-4 text-green-500">{message}</p>}
    </div>
  );
};

export default TaskComponent;
